#import librariesimport pandas as pdimport numpy as npfrom sklearn import preprocessingfrom sklearn.preprocessing import LabelEncoder#pull the clean data#pull the clean datadf = pd.read_csv('data_cleaned.csv')df_test=pd.read_csv(r'test_cleaned.csv')#Feature Engineering#Normalize all the numerical features and encode all the categorical features#training datascaled_features = df.copy()col_names = ['no_of_trainings', 'age','previous_year_rating','length_of_service','avg_training_score']label_names = ['department','education','recruitment_channel','region', 'gender', 'awards_won?']features = scaled_features[col_names]scaler = preprocessing.StandardScaler().fit(features.values)features = scaler.transform(features.values)scaled_features = pd.get_dummies(scaled_features, columns=label_names, drop_first=True)scaled_features[col_names] = featuresscaled_features.drop(columns=['employee_id'],inplace=True)#test datascaled_features_test = df_test.copy()features_test = scaled_features_test[col_names]scaler_test = preprocessing.StandardScaler().fit(features_test.values)features_test = scaler_test.transform(features_test.values)scaled_features_test = pd.get_dummies(scaled_features_test, columns=label_names, drop_first=True)scaled_features_test[col_names] = features_testscaled_features_test.drop(columns=['employee_id'],inplace=True)#save scaled_features dataframescaled_features.to_csv('data_scaled.csv', index=False)scaled_features_test.to_csv('test_scaled.csv', index=False)